package com.zhongzhou.dto;

import java.util.Date;


/**
 *	菜单点赞 Dto
 */
public class MenuFeedbackDto extends MenuDto{

	public MenuFeedbackDto() {
		super();
		// TODO Auto-generated constructor stub
	}

	/**
	 * 	菜单点赞id
	 */
	private Integer menuFeedbackId;
	
	/**
	 *  1好评 2差评
	 */
	private Integer goodState;
	
	/**
	 *  添加时间或修改时间    只算当天的
	 */
	private Date goodTime;
	
	/**
	 *	 
	 */
	private String wxid;

	
	
	
	
	/**
	 * @return
	 */
	public Integer getMenuFeedbackId() {
		return menuFeedbackId;
	}

	public void setMenuFeedbackId(Integer menuFeedbackId) {
		this.menuFeedbackId = menuFeedbackId;
	}

	public Integer getGoodState() {
		return goodState;
	}

	public void setGoodState(Integer goodState) {
		this.goodState = goodState;
	}

	public Date getGoodTime() {
		return goodTime;
	}

	public void setGoodTime(Date goodTime) {
		this.goodTime = goodTime;
	}

	public String getWxid() {
		return wxid;
	}

	public void setWxid(String wxid) {
		this.wxid = wxid;
	}
	
	
	
	
	
	
}
